import logE from '../logE';
export default (Vue) => {
    Vue.directive('logE', {
        bind(el, binding) {
            el.addEventListener('click', () => {
                // const data = binding.value;
                // const eventId = parseInt(data.eventId, 10);
                // logE(eventId, data.ext);
                const data = binding.value;
                if (Array.isArray(data)) {
                    logE.apply(null, data);
                }
                else {
                    const eventId = parseInt(data.eventId, 10);
                    const event = data.event;
                    logE(event || eventId, data.ext);
                }
            });
        },
    });
};
