import logE from "../logE";

export default (Vue) => {
    Vue.directive('log-elementClick', {
        bind(el, binding, vnode, oldVnode) {
            el.addEventListener('click', () => {
                const element = el;
                const content = el.innerText;
                const tagName = el.tagName;
                const image = tagName === 'IMG' ? el.src : '';
                const defaultData = {element, content, image};
                const data = binding.value;

                logE('elementClick', {
                    ...defaultData, ...data
                });
            });
        },
    });
};

// v-log-click
