// window.name 统一管理
import { fn, ua } from "@wanwu/base-fn";

const crossUtil = {
    windowName: "{}",

    initWindowName() {
        window.name = this.windowName;
    },

    getWindowName():string {
        return window.name || this.windowName;
    },

    // 可能是string 或者 {}
    getWindowNameItem(name: string) {
        const currentObj = JSON.parse(this.getWindowName());
        return currentObj[name];
    },

    setWindowName(editObj: {}) {
        try {
            let currentObj = JSON.parse(this.getWindowName());
            if (Object.prototype.toString.call(currentObj) !== "[object Object]") {
                currentObj = JSON.parse(this.windowName);
            }
            if (Object.prototype.toString.call(editObj) !== "[object Object]") {
                editObj = {};
            }
            const windowNameObj = {
                ...currentObj,
                ...editObj
            }
            window.name = JSON.stringify(windowNameObj);
        } catch (err) {
            console.log(err)
        }
    },
};

export default crossUtil;
