<template>
    <popup
        class="component__dialog dialog"
        :value="value"
        :overlay="overlay"
        :close-on-click-overlay="closeOnClickOverlay"
        @input="onPopInput"
        @click-overlay="clickOverlay"
        @closed="onClosed"
    >
        <div
            class="content"
            :style="{ width:width / 100 + 'rem',marginBottom:offsetTop && (offsetTop / 50 + 'rem') }"
        >
            <slot name="title">
                <div
                    v-if="title"
                    :style="titleStyle"
                    class="title"
                    v-text="title"
                ></div>
            </slot>
            <slot>
                <div
                    class="message"
                    :style="messageStyle"
                    v-text="message"
                ></div>
            </slot>
            <slot name="footer">
                <div class="footer flex">
                    <button
                        v-if="showCancelButton"
                        class="btn footer__btn f-fg-1"
                        :style="{ color: cancelButtonColor }"
                        @click="cancel"
                    >
                        {{ cancelButtonText }}
                    </button>
                    <button
                        v-if="showConfirmButton"
                        class="btn footer__btn footer__btn--confirm f-fg-1"
                        :style="{ color: confirmButtonColor }"
                        @click="confirm"
                    >
                        {{ confirmButtonText }}
                    </button>
                </div>
            </slot>
        </div>
    </popup>
</template>

<script>
    import popup from '@wanwu/base-vue-popup';
    import '@wanwu/base-style';
    export default {
        name: 'Dialog',
        components: {
            popup,
        },
        props: {
            value: {
                type: Boolean,
                default: false,
            },
            title: {
                type: String,
            },
            message: {
                type: String,
            },
            showConfirmButton: {
                type: Boolean,
                default: true,
            },
            showCancelButton: {
                type: Boolean,
                default: false,
            },
            cancelButtonText: {
                type: String,
                default: '取消',
            },
            cancelButtonColor: {
                type: String,
                default: '#737373',
            },
            confirmButtonText: {
                type: String,
                default: '知道了', /// /////////
            },
            confirmButtonColor: {
                type: String,
                default: '#007aff',
            },
            overlay: {
                type: Boolean,
                default: true,
            },
            closeOnClickOverlay: {
                type: Boolean,
                default: false,
            },
            beforeClose: {
                type: Function,
            },
            width: {
                type: Number,
            },
            offsetTop: {
                type: Number,
            },
            titleStyle: {
                type: [
                    Object,
                    String,
                ],
            },
            messageStyle: {
                type: [
                    Object,
                    String,
                ],
            },
        },
        data() {
            return {};
        },
        watch: {
            value(val) {
                if (val) {
                    this.$emit('show');
                } else {
                    this.$emit('close');
                }
            },
        },
        methods: {
            onClosed() {
                this.$emit('closed');
            },
            onPopInput(val) {
                this.$emit('input', val);
            },
            clickOverlay() {
                this.$emit('click-overlay');
            },
            confirm() {
                this.$emit('confirm');
                if (this.onConfirm) {
                    this.onConfirm();
                }
                if (this.beforeClose) {
                    this.beforeClose('confirm', this.close);
                } else {
                    this.close();
                }
            },
            cancel() {
                this.$emit('cancel');
                if (this.onCancel) {
                    this.onCancel();
                }
                if (this.beforeClose) {
                    this.beforeClose('cancel', this.close);
                } else {
                    this.close();
                }
            },
            close(isColse) {
                if (isColse !== false) {
                    this.$emit('input', false);
                    if (this.onClose) {
                        this.onClose();
                    }
                }
            },
        },
    };
</script>

<style lang='less' scoped>
    .component__dialog {
        .content {
            width: 560px;
            background: #fafafa;
            border-radius: 20px;
            padding-top: 54px;
        }

        .title {
            font-size: 34px;
            font-weight: 400;
            color: rgba(3, 3, 3, 1);
            line-height: 48px;
            text-align: center;
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;
        }

        .message {
            font-size: 26px;
            font-weight: 400;
            color: rgba(74, 74, 74, 1);
            line-height: 40px;
            text-align: center;
            margin-top: 8px;
            min-height: 76px;
            margin-bottom: 38px;
            padding: 0 60px;
            white-space: pre-wrap;
        }

        .footer {
            border-top: 2px solid #eee;

            &__btn {
                font-size: 32px;
                font-weight: 400;
                line-height: 88px;
                width: 100%;
                padding: 0 20px;
                white-space: nowrap;

                &:not(:first-child) {
                    border-left: 2px solid #eee;
                }
            }
        }
    }
</style>
