import 'core-js/modules/es.array.filter';

/**
 *  数字转rem
 * @param {Number} value
 */
var filters = function filters(value) {
  if (value) {
    return value / 100 + 'rem';
  } else {
    return undefined;
  }
};

filters.install = function (Vue) {
  Vue.filter('numToRem', filters);
};

export default filters;
