<template>
    <Popup
        class="component__loadingToast loadingToast"
        :value="value"
        :overlay="false"
        :slot-style="{ zIndex:'4001' }"
        @closed="closed($event)"
        @input="onInput"
    >
        <div class="content flex f-fd-c f-jc-c f-ai-c">
            <Loading
                :size="80"
                class="Loading"
                :load-type="loadType"
                v-bind="loadingProps"
            ></Loading>
            <div
                v-if="loadingText"
                class="text"
            >
                {{ loadingText }}
            </div>
        </div>
    </Popup>
</template>

<script>
    import Popup from '@wanwu/base-vue-popup';
    import Loading from '@wanwu/base-vue-loading';
    export default {
        name: 'LoadingToast',
        components: {
            Popup,
            Loading,
        },
        props: {
            value: {
                type: Boolean,
                default: false,
            },
            loadingText: {
                type: String,
                default: '',
            },
            loadType: {
                type: String,
                default: 'line',
            },
            loadingProps: {
                type: Object,
            },
        },
        data() {
            return {};
        },

        computed: {},
        methods: {
            closed(e) {
                this.$emit('closed');
            },
            onInput(val) {
                this.$emit('input', val);
            },
        },
    };
</script>

<style lang='less' scoped>
.component__loadingToast {
    .content {
        max-width: 2.26rem;
        min-width: 1.8rem;
        min-height: 1.8rem;
