<template>
    <span
        class="component__locationLink locationLink"
        @click="locationTo"
    >
        <slot></slot>
    </span>
</template>

<script>
    import wLocation from '@wanwu/w-location';
    export default {
        name: 'LocationLink',
        props: {
            to: {
                type: [
                    String,
                    Object,
                ],
            },
            replace: {
                type: Boolean,
            },
        },
        methods: {
            locationTo() {
                if (!this.$location) {
                    this.$location = wLocation;
                }
                this.$location[this.replace ? 'replace' : 'push'](this.to);
            },
        },
    };
</script>
