<template>
    <div
        class="component__Occupying Occupying"
        :style="{ height: remHeight, backgroundColor: bgColor }"
    >
        <slot></slot>
    </div>
</template>
<script>
    export default {
        name: 'Occupying',
        props: {
            height: {
                default: 20,
                type: [
                    Number,
                    String,
                ],
            },
            bgColor: {
                default: '',
                type: String,
            },
        },
        computed: {
            remHeight() {
                return this.height / 100 + 'rem';
            },
        },
    };
</script>

<style lang="less" scoped>
    .component__Occupying {
        width: 100%;
    }
</style>
