<template>
    <div class="component__swipeItem swipeItem" :style="style" v-on="$listeners">
        <slot></slot>
    </div>
</template>

<script>
    export default {
        name: 'SwipeItem',
        components: {

        },
        props: {

        },
        data() {
            return {
                offset: 0,
            };
        },

        computed: {
            style() {
                const { vertical, computedWidth, computedHeight } = this.$parent;
                return {
                    width: computedWidth ? computedWidth + 'px' : '',
                    height: vertical ? computedHeight + 'px' : '100%',
                    transform: `translate${vertical ? 'Y' : 'X'}(${this.offset}px)`,
                };
            },
        },
        methods: {

        },
        beforeCreate() {
            this.$parent.swipes.push(this);
        },

        destroyed() {
            this.$parent.swipes.splice(this.$parent.swipes.indexOf(this), 1);
        },
    };
</script>

<style lang='less' scoped>
.component__swipeItem {
    float: left;
    height: 100%;
}
</style>
