<template>
    <div
        v-show="show"
        class="component__tabsPane tabsPane"
    >
        <slot></slot>
        <div
            v-if="$slots.tab"
            ref="tabs"
            class="tabs-label"
        >
            <div class="tabs-label--content">
                <slot name="tab"></slot>
            </div>
        </div>
    </div>
</template>

<script>
    export default {
        name: 'TabsPane',
        components: {},
        props: {
            name: {
                type: [
                    String,
                    Number,
                ],
            },
            label: {
                type: [
                    String,
                    Number,
                ],
                default: '',
            },
            // 传入图片
            iconImage: {
                type: String,
                default: '',
            },
            // 传入阿里图标
            iconFont: {
                type: String,
                default: '',
            },
            // 是否可点击
            isClick: {
                type: Boolean,
                default: true,
            },
        },
        data() {
            return {
                show: false,
            };
        },
        computed: {
            active() {
                return this.$parent.value === this.name;
            },
        },
        watch: {
            label() {
                this.updateNav();
            },
            iconFont() {
                this.updateNav();
            },
            iconImage() {
                this.updateNav();
            },
            name() {
                this.updateNav();
            },
            isClick() {
                this.updateNav();
            },
        },
        mounted() {
            // console.log(this.$parent.$slots.default);
            const index = this.$parent.$slots.default.indexOf(this.$vnode);
            const navList = this.$parent.navList;
            navList.splice(index === -1 ? navList.length : index, 0, this);
            if (this.$slots.tab) {
                this.$parent.renderTitle(this.$refs.tabs, index);
            }
            this.updateNav();
        },
        methods: {
            updateNav() {
                this.$parent.updateNav();
            },
        },

    };
</script>

<style lang='less' scoped>
.tabs-label--content {
    display: inline-block;
}
</style>
