import { fn } from '@wanwu/base-fn';
/**
 * 获取api的env参数
 */
export function getApiEnv() {
    return fn.getQueryString('__env');
}
/**
 * 获取前端的env参数
 * 不符合`/^https?:\/\/(.+)(\.|-)(h5|m)\.wanwudezhi\.com/`的域名会返回当前域名
 */
export function getFeEnv() {
    return location.origin.replace(/^https?:\/\/(.+)(\.|-)(h5|m)\.wanwudezhi\.com/, `$1`);
}
/**
 * 是否是后端预发
 */
export function isApiPre() {
    return getApiEnv() === 'pre';
}
/**
 * 是否是后端测试
 */
export function isApiDev() {
    return getApiEnv() === 'develop';
}
/**
 * 是否是后端线上
 */
export function isApiProd() {
    return !isApiPre() && !isApiDev();
}
/**
 * 是否是前端预发
 */
export function isFePre() {
    return getFeEnv() === 'pre';
}
/**
 * 是否是前端测试
 */
export function isFeDev() {
    return getFeEnv() === 'dev';
}
/**
 * 是否是前端线上
 */
export function isFeProd() {
    return !isFePre() && !isFeDev();
}

export default {
    getApiEnv,
    getFeEnv,
    isApiPre,
    isApiDev,
    isApiProd,
    isFePre,
    isFeDev,
    isFeProd,
};
