import { fn, ua } from '@wanwu/base-fn';
import { isToControlVersion } from './utils';

function inject() {
    const script = document.createElement('script');

    if (ua.isIOSApp() && (isToControlVersion() && fn.compareVersion(ua.getAppVersion(), '2.2.0') < 0)) {
        script.src = 'https://cdn.wanwudezhi.com/static/mall-web/assets/IOSCordova.js';
    // return;
    } else if (ua.isAndroidApp()) {
    // script.src = 'https://cdn.wanwudezhi.com/AndroidCordova.js';
        return;
    } else if (ua.isWechat()) {
    // script.src = 'https://res.wx.qq.com/open/js/jweixin-1.4.0.js';
        return;
    } else {
        return;
    }

    document.body.appendChild(script);
}

inject();
