/**
 * 动态注入cdn依赖
 * @param {String} src cdn依赖的url
 * @returns {Promise}
 */

function inject(src) {
    return new Promise((resolve, reject) => {
        const script = document.createElement('script');
        script.src = src;
        document.body.appendChild(script);
        script.onload = () => {
            resolve();
        };
        script.onerror = (err) => {
            reject(err);
        };
    });
}

export default inject;
