// ./test/UppercaseProxy.spec.js
import mockAxios from 'jest-mock-axios';
import { kl_request } from '..';
afterEach(function () {
  // cleaning up the mess left behind the previous test
  mockAxios.reset();
});
it('should work', function () {
  var catchFn = jest.fn(),
      thenFn = jest.fn();
  kl_request.get('/a', {}, {}).then(thenFn)["catch"](catchFn);
  expect(mockAxios.get).toHaveBeenCalledWith('/a', {});
});