import { logE } from '@wanwu/base-logger';
import mySentry from '@wanwu/sentry';
export default {
  install: function install(Vue) {
    // 收集错误日志信息
    Vue.prototype.$logError = mySentry.logError;
    Vue.prototype.$setMySentryError = mySentry.setMySentryError;
    Vue.prototype.$setUserOrTag = mySentry.setUserOrTag; // 手动埋点收集日志信息

    Vue.prototype.$logE = logE;
  }
};