import ShareDialog from '@wanwu/base-vue-share-dialog';
import LoadingToast from '@wanwu/base-vue-loading-toast';
import Dialog from '@wanwu/base-vue-dialog';
import FImage from '@wanwu/base-vue-f-image';
export default {
  install: function install(Vue) {
    Vue.use(ShareDialog);
    Vue.use(LoadingToast);
    Vue.use(Dialog);
    Vue.use(FImage);

    Vue.$allUiClose = function () {
      Vue.share.close();
      Vue.share.remove();
      Vue.loadingToast.close();
      Vue.dialog.close();
    };
  }
};