import h5_axios, { cancel, initSource, axios } from './wr-local';
import User from '@wanwu/base-user'; // import apiResultHandler from './apiResultHandler';

window.hasGotoLogin = false;
axios.setOption({
  cbFor401: function cbFor401() {
    if (!window.hasGotoLogin) {
      window.hasGotoLogin = true;
      setTimeout(function () {
        window.hasGotoLogin = false;
      }, 3000);
      User.goToLogin();
    }
  } // responseInterceptor: [apiResultHandler],

}); // axios.initAxios();

export { cancel, initSource };
export default h5_axios;