export function getPageScroll() {
    let xScroll, yScroll;
    if (document.documentElement && document.documentElement.scrollTop) { // Explorer 6 Strict
        yScroll = document.documentElement.scrollTop;
        xScroll = document.documentElement.scrollLeft;
    } else if (document.body) { // all other Explorers
        yScroll = document.body.scrollTop;
        xScroll = document.body.scrollLeft;
    }
    const ObjPageScroll = { x: xScroll, y: yScroll };
    return ObjPageScroll;
}
export function setPageScroll(x, y) {
    let xScroll, yScroll;
    if (document.documentElement && document.documentElement.scrollTop) { // Explorer 6 Strict
        yScroll = document.documentElement.scrollTop = y;
        xScroll = document.documentElement.scrollLeft = x;
    } else if (document.body) { // all other Explorers
        yScroll = document.body.scrollTop = y;
        xScroll = document.body.scrollLeft = y;
    }
    const ObjPageScroll = { x: xScroll, y: yScroll };
    return ObjPageScroll;
}
