// import * as Sentry from '@sentry/browser'
import { ERROR_TYPE, ERROR_LEVEL } from './utils'
// sentry error ç±»
export default class MyError extends Error {
    type: string
    level: any
    extra?: any
    tag?: any
    ConfirmReport?: boolean | any
    constructor(err: any) {
        super()
        this.name = err.name || err.ActionStatus || ''
        this.message = err.errorInfo || err.message || ''
        this.type = err.type || ERROR_TYPE.Javascript
        this.ConfirmReport ? (this.ConfirmReport = err.ConfirmReport) : ''
        this.tag && (this.tag = err.tag)
        err.stack && (this.stack = err.stack)
        err.extra && (this.extra = err.extra)
        err.ErrorInfo ? (this.level = ERROR_LEVEL.Warning) : (this.level = err.level || ERROR_LEVEL.Error)
    }
}
